const input = $input.first().json.body || $input.first().json;
const chatId = input.chatId || '!WaBTMZOwtYcfbNazWO:beeper.com';
const maxPages = input.maxPages || 5;

// Fetch messages directly
const results = [];
let cursor = null;
let page = 0;

while (page < maxPages) {
  const payload = {
    jsonrpc: '2.0',
    id: 1,
    method: 'tools/call',
    params: {
      name: 'list_messages',
      arguments: { chatID: chatId, cursor: cursor, direction: 'before' }
    }
  };

  try {
    const resp = await fetch('https://beeper-mcp.garzahive.com/v0/mcp', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    });
    const data = await resp.json();
    const text = data.result?.content?.[0]?.text || '{}';
    const parsed = JSON.parse(text);

    const messages = parsed.messages || [];
    cursor = parsed.cursor;
    const hasMore = parsed.hasMore;

    // Extract credentials
    const patterns = [
      { type: 'password', regex: /(?:password|pwd|pass)[:\s]*([^\s]{6,})/gi },
      { type: 'api_key', regex: /(sk-[a-zA-Z0-9]{20,}|shpat_[a-zA-Z0-9]{32,})/g },
      { type: 'recovery', regex: /([A-Z0-9]{5}-[A-Z0-9]{5})/g },
      { type: 'credential', regex: /([a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+)\s*[\/|:]\s*([^\s]{6,})/g },
      { type: 'hex', regex: /([a-f0-9]{32,})/gi },
      { type: 'jwt', regex: /(eyJ[a-zA-Z0-9_-]+\.[a-zA-Z0-9_-]+\.[a-zA-Z0-9_-]+)/g }
    ];

    for (const msg of messages) {
      const body = msg.body || msg.text || '';
      for (const p of patterns) {
        const matches = [...body.matchAll(p.regex)];
        for (const m of matches) {
          results.push({ type: p.type, value: m[1] || m[0], context: body.slice(0, 150) });
        }
      }
    }

    page++;
    if (!hasMore) break;
  } catch (e) {
    break;
  }
}

// Dedupe
const seen = new Set();
const unique = results.filter(r => {
  if (seen.has(r.value)) return false;
  seen.add(r.value);
  return true;
});

return [{ json: { success: true, pages: page, total: unique.length, credentials: unique } }];

